import React, { useState, useEffect } from "react";
import { format } from "date-fns";
import "../../assets/css/Calendar.css";

function Calendar() {
  const daysOfWeek = [
    "Domingo",
    "Segunda",
    "Terça",
    "Quarta",
    "Quinta",
    "Sexta",
    "Sábado",
  ];

  const months = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ];

  const [materias, setMaterias] = useState([]);
  useEffect(() => {
    const storedMaterias = JSON.parse(localStorage.getItem("materia")) || [];
    setMaterias(storedMaterias);

    return () => {};
  }, []);

  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDay, setSelectedDay] = useState(null);

  const handleDayClick = (day) => {
    setSelectedDay(day);
  };

  const events = {};

  // Função para filtrar matérias no intervalo de datas
  const filterMateriasByDateRange = (startDate, endDate) => {
    return materias.filter((materia) => {
      const materiaDate = new Date(materia.dataMateria); // Certifique-se de ter uma propriedade 'dataMateria' em sua estrutura de objeto de matéria

      // Verifica se a data da matéria está dentro do intervalo
      return materiaDate >= startDate && materiaDate <= endDate;
    });
  };

  // ...

  // Dentro da sua função 'Calendar', antes do retorno do JSX, adicione o seguinte trecho de código:

  // Defina a data inicial como o primeiro dia do mês
  const startDate = new Date(
    currentDate.getFullYear(),
    currentDate.getMonth(),
    1
  );

  // Defina a data final como o último dia do mês
  const endDate = new Date(
    currentDate.getFullYear(),
    currentDate.getMonth() + 1,
    0
  );

  // Filtra as matérias dentro do intervalo de datas
  const materiasNoIntervalo = filterMateriasByDateRange(startDate, endDate);

  const prevMonth = () => {
    setCurrentDate(
      new Date(currentDate.getFullYear(), currentDate.getMonth() - 1, 1)
    );
  };

  const nextMonth = () => {
    setCurrentDate(
      new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 1)
    );
  };

  return (
    <div className="calendar">
      <div className="calendar-header">
        <button onClick={prevMonth}>&lt;</button>
        <h2>
          {months[currentDate.getMonth()]} {currentDate.getFullYear()}
        </h2>
        <button onClick={nextMonth}>&gt;</button>
      </div>
      <table className="calendar-table">
        <thead>
          <tr>
            {daysOfWeek.map((day) => (
              <th key={day}>{day}</th>
            ))}
          </tr>
        </thead>
        <tbody className="calendarHome">
          {getDaysInMonth(currentDate).map((week, rowIndex) => (
            <tr key={rowIndex}>
              {week.map((day, colIndex) => (
                <td
                  key={colIndex}
                  onClick={() =>
                    handleDayClick(`${format(currentDate, "yyyy-MM")}-${day}`)
                  }
                >
                  <div className="dayContainer">{day}</div>
                  <div className="materiasContainer">
                    {day !== "" &&
                      materiasNoIntervalo.map((materia, index) => {
                        console.log(
                          "verificação 1 " +
                            materia.horarioMateria +
                            "-" +
                            materia.periodoMateria +
                            "-" +
                            materia.professorMateria +
                            "-" +
                            materia.salaMateria
                        );
                        return (
                          <div key={index}>
                            {console.log(
                              "verificação 1 " +
                                materia.horarioMateria +
                                "-" +
                                materia.periodoMateria +
                                "-" +
                                materia.professorMateria +
                                "-" +
                                materia.salaMateria
                            )}
                            {materia.horarioMateria} - {materia.periodoMateria}{" "}
                            - {materia.professorMateria} - {materia.salaMateria}
                          </div>
                        );
                      })}
                  </div>
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Calendar;
