import React, { useState, useEffect } from 'react';
import { format } from 'date-fns';
import '../../assets/css/Calendar.css';

function Calendar() {
  const daysOfWeek = ['domingo', 'segunda', 'terça', 'quarta', 'quinta', 'sexta', 'sábado'];
  const months = [
    'January',
    'February',
    'March',
    'April',
    'May',
    'June',
    'July',
    'August',
    'September',
    'October',
    'November',
    'December',
  ];

  const [materias, setMaterias] = useState([]);
  useEffect(() => {
    const storedMaterias = JSON.parse(localStorage.getItem('materia')) || [];
    setMaterias(storedMaterias);

    return () => {};
  }, []);

  const [currentDate, setCurrentDate] = useState(new Date());
  const [popupVisible, setPopupVisible] = useState(false);
  const [selectedDay, setSelectedDay] = useState(null);

  const events = {};

  materias.forEach((materia) => {
    const dataInicio = new Date(materia.dataInicioMateria);
    const dataFim = new Date(materia.dataFimMateria);
    const diaSemanaMateria = materia.diaSemanaMateria;

    for (let date = dataInicio; date <= dataFim; date.setDate(date.getDate() + 1)) {
      const diaSemana = daysOfWeek[date.getDay()];

      if (!events[diaSemana]) {
        events[diaSemana] = [];
      }

      if (!events[diaSemana].some((event) => event.id === materia.id) && diaSemana === diaSemanaMateria) {
        events[diaSemana].push(materia);
      }
    }
  });

  const getDaysInMonth = (date) => {
    const year = date.getFullYear();
    const month = date.getMonth();
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const startDay = firstDay.getDay();
    const endDay = lastDay.getDate();

    const days = [];
    let currentWeek = [];

    for (let i = 0; i < startDay; i++) {
      currentWeek.push('');
    }

    for (let day = 1; day <= endDay; day++) {
      currentWeek.push(day);
      if (currentWeek.length === 7) {
        days.push(currentWeek);
        currentWeek = [];
      }
    }

    if (currentWeek.length > 0) {
      while (currentWeek.length < 7) {
        currentWeek.push('');
      }
      days.push(currentWeek);
    }

    return days;
  };

  const prevMonth = () => {
    setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() - 1, 1));
  };

  const nextMonth = () => {
    setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 1));
  };

  const handleDayClick = (day) => {
    setSelectedDay(day);
    setPopupVisible(true);
  };

  const obterDiasNoMes = (data) => {
    const ano = data.getFullYear();
    const mes = data.getMonth();
    const primeiroDia = new Date(ano, mes, 1);
    const ultimoDia = new Date(ano, mes + 1, 0);
    const diaInicio = primeiroDia.getDay();
    const diaFim = ultimoDia.getDate();

    const dias = [];
    let semanaAtual = [];

    for (let i = 0; i < diaInicio; i++) {
      semanaAtual.push(null); // Use null para células vazias
    }

    for (let dia = 1; dia <= diaFim; dia++) {
      semanaAtual.push(dia);
      if (semanaAtual.length === 7) {
        dias.push(semanaAtual);
        semanaAtual = [];
      }
    }

    if (semanaAtual.length > 0) {
      while (semanaAtual.length < 7) {
        semanaAtual.push(null); // Use null para células vazias
      }
      dias.push(semanaAtual);
    }

    return dias;
  };

  return (
    <div className="calendar">
      <div className="calendar-header">
        <button onClick={prevMonth}>&lt;</button>
        <h2>{months[currentDate.getMonth()]} {currentDate.getFullYear()}</h2>
        <button onClick={nextMonth}>&gt;</button>
      </div>
      <table className="calendar-table">
        <thead>
          <tr>
            {daysOfWeek.map((day) => (
              <th key={day}>{day}</th>
            ))}
          </tr>
        </thead>
        <tbody className='calendarHome'>
          {getDaysInMonth(currentDate).map((week, rowIndex) => (
            <tr key={rowIndex}>
              {week.map((day, colIndex) => (
                <td key={colIndex} onClick={() => handleDayClick(`${format(currentDate, 'dd/MM/yyyy')}-${day}`)}>
                  <div className='dayContainer'>
                    {day}
                  </div>
                  {events[daysOfWeek[colIndex]] && events[daysOfWeek[colIndex]].map((materia, index) => (
                    <div key={index} className='materiasContainer'>
                      {materia.nomeMateria} - {materia.horarioMateria} -{materia.professorMateria}
                    </div>
                  ))}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Calendar;
