  //     console.log(`Dia da semana esperado: ${diaSemana == diaSemanaMateria}`);
  //     console.log(`________________________`);
  //     console.log(`1 ${date >= dataInicio}`);
  //     console.log(` 2${date <= dataFim}`);
  //     console.log(`________________________`);
