import React, { useEffect, useState } from 'react';
import '../../assets/css/Calendar.css';

function Calendar() {
  const daysOfWeek = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
  const months = [
    'January',
    'February',
    'March',
    'April',
    'May',
    'June',
    'July',
    'August',
    'September',
    'October',
    'November',
    'December',
  ];
  const [materias, setMaterias] = useState([]);
  useEffect(() => {
    const storedMaterias = JSON.parse(localStorage.getItem("materia")) || [];
    setMaterias(storedMaterias);
  });
  const [currentDate, setCurrentDate] = useState(new Date());
  const [popupVisible, setPopupVisible] = useState(false);
  const [selectedDay, setSelectedDay] = useState(null); 
  const [events, setEvents] = useState({
    '2023-11-01': ['Matéria 1', 'Matéria 2'],
    '2023-11-05': ['Matéria 3'],

  });



  const getDaysInMonth = (date) => {
    const year = date.getFullYear();
    const month = date.getMonth();
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const startDay = firstDay.getDay();
    const endDay = lastDay.getDate();

    const days = [];
    let currentWeek = [];

    for (let i = 0; i < startDay; i++) {
      currentWeek.push('');
    }

    for (let day = 1; day <= endDay; day++) {
      currentWeek.push(day);
      if (currentWeek.length === 7) {
        days.push(currentWeek);
        currentWeek = [];
      }
    }

    if (currentWeek.length > 0) {
      // Preencher a última semana do mês, se necessário
      while (currentWeek.length < 7) {
        currentWeek.push('');
      }
      days.push(currentWeek);
    }

    return days;
  };

  const prevMonth = () => {
    setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() - 1, 1));
  };

  const nextMonth = () => {
    setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 1));
  };

  const handleDayClick = (day) => {
    setSelectedDay(day);
    ma
    setPopupVisible(true);
  };

  const addMateria = (materia) => {
    const updatedMaterias = [...materias, materia];
    setMaterias(updatedMaterias);
    localStorage.setItem("materia", JSON.stringify(updatedMaterias));
  };

  const removeMateria = (materia) => {
    const updatedMaterias = materias.filter((m) => m !== materia);
    setMaterias(updatedMaterias);
    localStorage.setItem("materia", JSON.stringify(updatedMaterias));
  };

  return (
    <div className="calendar">
      <div className="calendar-header">
        <button onClick={prevMonth}>&lt;</button>
        <h2>{months[currentDate.getMonth()]} {currentDate.getFullYear()}</h2>
        <button onClick={nextMonth}>&gt;</button>
      </div>
      <table className="calendar-table">
        <thead>
          <tr>
            {daysOfWeek.map((day) => (
              <th key={day}>{day}</th>
            ))}
          </tr>
        </thead>
        <tbody className='calendarHome'>
          {getDaysInMonth(currentDate).map((week, rowIndex) => (
            <tr key={rowIndex}>
              {week.map((day, colIndex) => (
                <td key={colIndex} onClick={() => handleDayClick(`${currentDate.getFullYear()}-${currentDate.getMonth() + 1}-${day}`)}>
                  <div className='dayContainer' >
                    {day}
                  </div>
                  <div className='materiasContainer'>
                    {materias.map((materia, index) => (
                      <div key={index}>
                        {materia}
                        <button onClick={() => removeMateria(materia)}>Remover</button>
                      </div>
                    ))}
                    <button onClick={() => addMateria("Nova Matéria")}>Adicionar Matéria</button>
                  </div>
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>

      {popupVisible && (
        <div className="popup">
          <div className="popup-content">
            <span className="close" onClick={closePopup}>&times;</span>
            <h3>{selectedDay}</h3>
            <ul>
              {materias.map((materia, index) => (
                <li key={index}>{materia}</li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
}

export default Calendar;
