import React, { useState, useEffect } from "react";
import Header from "../../component/Header/Header";
import "../../assets/css/FormMateria.css";

function FormMateria() {
  const [materias, setMaterias] = useState([]);
  const [periodos, setPeriodos] = useState([]);
  const [professores, setProfessores] = useState([]);
  const [salas, setSalas] = useState([]);

  const [novaMateria, setNovaMateria] = useState({
    nome: "",
    periodosMateria: [],
    professorMateria: "",
    dataInicioMateria: "",
    dataFimMateria: "",
    diaSemanaMateria: "",
    horarioMateria: "",
    salaMateria: "",
  });

  useEffect(() => {
    // Carregar dados iniciais, como períodos, professores, salas, etc.
    // Exemplo: carregarPeriodos(), carregarProfessores(), carregarSalas()
  }, []);

  const adicionarMateria = () => {
    try {
      if (
        novaMateria.nome &&
        novaMateria.periodosMateria.length > 0 &&
        novaMateria.professorMateria &&
        novaMateria.dataInicioMateria &&
        novaMateria.dataFimMateria &&
        novaMateria.diaSemanaMateria &&
        novaMateria.horarioMateria &&
        novaMateria.salaMateria
      ) {
        const updatedMaterias = [...materias, { ...novaMateria }];
        setMaterias(updatedMaterias);
        setNovaMateria({
          nome: "",
          periodosMateria: [],
          professorMateria: "",
          dataInicioMateria: "",
          dataFimMateria: "",
          diaSemanaMateria: "",
          horarioMateria: "",
          salaMateria: "",
        });
      } else {
        alert("Preencha todos os campos!");
      }
    } catch (error) {
      console.error("Erro ao adicionar matéria", error);
    }
  };
  return (
    <>
      <Header />
      <div className="section1FormMaterias">
        <h1 className="tituloPagina">Cadastro de Matérias</h1>
        <div className="containerFormMaterias">
          <div className="cadastroFormularioMaterias">
            <div className="divCampoMateria">
              <label className="labelCampoMateria">Nome da Matéria:</label>
              <input
                className="inputCampoMateria"
                placeholder="Digite o nome da matéria"
                id="nomeMateria"
              ></input>
            </div>
            <div className="divCampoMateria">
              <label className="labelCampoMateria">Períodos:</label>
              {/* Add logic to select multiple periods */}
              <select className="selectCampoMateria" id="periodosMateria">
                <option value="periodo1">Período 1</option>
                <option value="periodo2">Período 2</option>
                {/* Add more options as needed */}
              </select>
            </div>
            <div className="divCampoMateria">
              <label className="labelCampoMateria">Professor:</label>
              <select className="selectCampoMateria" id="professorMateria">
                {/* Add options dynamically based on the previously entered professors */}
                <option value="professor1">Professor 1</option>
                <option value="professor2">Professor 2</option>
                {/* Add more options as needed */}
              </select>
            </div>
            <div className="divCampoMateria">
              <label className="labelCampoMateria">Data de Início:</label>
              <input
                className="inputCampoMateria"
                placeholder="Digite a data de início"
                id="dataInicioMateria"
                type="date"
              ></input>
            </div>
            <div className="divCampoMateria">
              <label className="labelCampoMateria">Data de Fim:</label>
              <input
                className="inputCampoMateria"
                placeholder="Digite a data de fim"
                id="dataFimMateria"
                type="date"
              ></input>
            </div>
            <div className="divCampoMateria">
              <label className="labelCampoMateria">Dia da Semana:</label>
              <input
                className="inputCampoMateria"
                placeholder="Digite o dia da semana"
                id="diaSemanaMateria"
              ></input>
            </div>
            <div className="divCampoMateria">
              <label className="labelCampoMateria">Horário:</label>
              <input
                className="inputCampoMateria"
                placeholder="Digite o horário"
                id="horarioMateria"
              ></input>
            </div>
            <div className="divCampoMateria">
              <label className="labelCampoMateria">Sala:</label>
              <select className="selectCampoMateria" id="salaMateria">
                {/* Add options dynamically based on the previously entered rooms */}
                <option value="">Sala</option>
                {salas.map((sala) => (
                  <option key={sala.id} value={sala.id}>
                    {sala.nome}
                  </option>
                ))}
                {/* Add more options as needed */}
              </select>
            </div>
            <button type="submit" className="btnSubmitFormMaterias">
              Salvar
            </button>
          </div>
        </div>
        <div className="containerTabelaMaterias">
          <table>
            <thead>
              <tr>
                <th>Nome da Matéria</th>
                <th>Períodos</th>
                <th>Professor</th>
                <th>Data de Início</th>
                <th>Data de Fim</th>
                <th>Dia da Semana</th>
                <th>Horário</th>
                <th>Sala</th>
                <th></th>
                <th></th>
              </tr>
            </thead>
            <tbody></tbody>
          </table>
        </div>
      </div>
    </>
  );
}

export default FormMateria;
