import React, { useState, useEffect } from "react";
import Header from "../../component/Header/Header";
import "../../assets/css/FormMateria.css";

function FormMateria() {
  const [materias, setMaterias] = useState([]);
  const [periodos, setPeriodos] = useState([]);
  const [professores, setProfessores] = useState([]);
  const [salas, setSalas] = useState([]);

  const [novaMateria, setNovaMateria] = useState({
    nome: "",
    periodosMateria: [],
    professorMateria: "",
    dataInicioMateria: "",
    dataFimMateria: "",
    diaSemanaMateria: "",
    horarioMateria: "",
    salaMateria: "",
  });

  useEffect(() => {
    // Carregar dados iniciais, como períodos, professores, salas, etc.
    // Exemplo: carregarPeriodos(), carregarProfessores(), carregarSalas()
  }, []);

  const adicionarMateria = () => {
    try {
      if (
        novaMateria.nome &&
        novaMateria.periodosMateria.length > 0 &&
        novaMateria.professorMateria &&
        novaMateria.dataInicioMateria &&
        novaMateria.dataFimMateria &&
        novaMateria.diaSemanaMateria &&
        novaMateria.horarioMateria &&
        novaMateria.salaMateria
      ) {
        const updatedMaterias = [...materias, { ...novaMateria }];
        setMaterias(updatedMaterias);
        setNovaMateria({
          nome: "",
          periodosMateria: [],
          professorMateria: "",
          dataInicioMateria: "",
          dataFimMateria: "",
          diaSemanaMateria: "",
          horarioMateria: "",
          salaMateria: "",
        });
      } else {
        alert("Preencha todos os campos!");
      }
    } catch (error) {
      console.error("Erro ao adicionar matéria", error);
    }
  };
  return (
    <>
      <Header />
      <div className="section1FormMaterias">
        {/* Restante do código... */}
        <div className="divCampoMateria">
          <label className="labelCampoMateria">Períodos:</label>
          <select
            className="selectCampoMateria"
            id="periodosMateria"
            value={novaMateria.periodosMateria}
            onChange={(e) =>
              setNovaMateria({
                ...novaMateria,
                periodosMateria: Array.from(e.target.selectedOptions, (item) =>
                  parseInt(item.value)
                ),
              })
            }
            multiple
          >
            {periodos.map((periodo) => (
              <option key={periodo.id} value={periodo.id}>
                {periodo.nome}
              </option>
            ))}
          </select>
        </div>

        <div className="divCampoMateria">
          <label className="labelCampoMateria">Professor:</label>
          <select
            className="selectCampoMateria"
            id="professorMateria"
            value={novaMateria.professorMateria}
            onChange={(e) =>
              setNovaMateria({
                ...novaMateria,
                professorMateria: e.target.value,
              })
            }
          >
            <option value="">Selecione um professor</option>
            {professores.map((professor) => (
              <option key={professor.id} value={professor.id}>
                {professor.nome}
              </option>
            ))}
          </select>
        </div>

        <div className="divCampoMateria">
          <label className="labelCampoMateria">Sala:</label>
          <select
            className="selectCampoMateria"
            id="salaMateria"
            value={novaMateria.salaMateria}
            onChange={(e) =>
              setNovaMateria({
                ...novaMateria,
                salaMateria: e.target.value,
              })
            }
          >
            <option value="">Selecione uma sala</option>
            {salas.map((sala) => (
              <option key={sala.id} value={sala.id}>
                {sala.nome}
              </option>
            ))}
          </select>
        </div>
        <div className="containerTabelaMaterias">
          <table>
            <thead>
              <tr>
                <th>Nome da Matéria</th>
                <th>Períodos</th>
                <th>Professor</th>
                <th>Data de Início</th>
                <th>Data de Fim</th>
                <th>Dia da Semana</th>
                <th>Horário</th>
                <th>Sala</th>
                <th></th>
                <th></th>
              </tr>
            </thead>
            <tbody></tbody>
          </table>
        </div>
      </div>
    </>
  );
}

export default FormMateria;
