import React, { useState, useEffect } from "react";
import Header from "../../component/Header/Header";
import "../../assets/css/FormHorario.css"; // Certifique-se de importar o CSS correto

function FormHorario() {
  const [horarios, setHorarios] = useState([]);
  const [novoHorario, setNovoHorario] = useState({
    curso: "",
    periodo: "",
    materia: "",
    sala: "",
    professor: "",
  });
  const [horarioEditando, setHorarioEditando] = useState(null);
  const [editando, setEditando] = useState(false);

  useEffect(() => {
    const storedHorarios = JSON.parse(localStorage.getItem("horarios")) || [];
    setHorarios(storedHorarios);
  }, []);

  const adicionarHorario = () => {
    try {
      if (
        novoHorario.curso &&
        novoHorario.periodo &&
        novoHorario.materia &&
        novoHorario.sala &&
        novoHorario.professor
      ) {
        const updatedHorarios = [...horarios, { ...novoHorario }];
        setHorarios(updatedHorarios);
        setNovoHorario({
          curso: "",
          periodo: "",
          materia: "",
          sala: "",
          professor: "",
        });
        localStorage.setItem("horarios", JSON.stringify(updatedHorarios));
      } else {
        alert("Preencha todos os campos!");
      }
    } catch (error) {
      console.error("Erro ao adicionar horário", error);
    }
  };

  const preencherCamposDeEdicao = (index) => {
    const horarioParaEditar = horarios[index];
    setHorarioEditando(horarioParaEditar);
    setNovoHorario({
      curso: horarioParaEditar.curso,
      periodo: horarioParaEditar.periodo,
      materia: horarioParaEditar.materia,
      sala: horarioParaEditar.sala,
      professor: horarioParaEditar.professor,
    });
    setEditando(true);
  };

  const confirmarEdicao = () => {
    try {
      if (
        novoHorario.curso &&
        novoHorario.periodo &&
        novoHorario.materia &&
        novoHorario.sala &&
        novoHorario.professor
      ) {
        // Cria uma cópia da lista de horários
        const horariosAtualizados = [...horarios];
        // Substitui o horário antigo pelo horário editado na lista
        horariosAtualizados[horarios.indexOf(horarioEditando)] = novoHorario;
        // Atualiza o estado da lista de horários
        setHorarios(horariosAtualizados);
        // Limpa os estados relacionados à edição
        setHorarioEditando(null);
        setNovoHorario({
          curso: "",
          periodo: "",
          materia: "",
          sala: "",
          professor: "",
        });
        setEditando(false);
        // Atualiza os horários no localStorage
        localStorage.setItem("horarios", JSON.stringify(horariosAtualizados));
      } else {
        alert("Preencha todos os campos!");
      }
    } catch (error) {
      console.error("Erro ao confirmar edição", error);
    }
  };

  const cancelarEdicao = () => {
    // Limpe os estados após o cancelamento
    setHorarioEditando(null);
    setNovoHorario({
      curso: "",
      periodo: "",
      materia: "",
      sala: "",
      professor: "",
    });
    setEditando(false);
  };

  const excluirHorario = (index) => {
    const novosHorarios = horarios.filter((horario, i) => i !== index);
    setHorarios(novosHorarios);
    setHorarioEditando(null);
    setNovoHorario({
      curso: "",
      periodo: "",
      materia: "",
      sala: "",
      professor: "",
    });
    setEditando(false);
    localStorage.setItem("horarios", JSON.stringify(novosHorarios));
  };

  return (
    <>
      <Header />
      <div className="section1FormHorarios">
        <h1 className="tituloPagina">Cadastro Horário</h1>
        <div className="conatinerFormHorarios">
          <div className="cadastroFormularioHorarios">
            <div className="divCampoHorario">
              <label className="labelCampoHorario">Curso:</label>
              <input
                className="inputCampoHorario"
                placeholder="Digite o curso"
                value={novoHorario.curso}
                onChange={(e) =>
                  setNovoHorario({ ...novoHorario, curso: e.target.value })
                }
              />
            </div>
            <div className="divCampoHorario">
              <label className="labelCampoHorario">Período:</label>
              <input
                className="inputCampoHorario"
                placeholder="Digite o período"
                value={novoHorario.periodo}
                onChange={(e) =>
                  setNovoHorario({ ...novoHorario, periodo: e.target.value })
                }
              />
            </div>
            <div className="divCampoHorario">
              <label className="labelCampoHorario">Matéria:</label>
              <input
                className="inputCampoHorario"
                placeholder="Digite a matéria"
                value={novoHorario.materia}
                onChange={(e) =>
                  setNovoHorario({ ...novoHorario, materia: e.target.value })
                }
              />
            </div>
            <div className="divCampoHorario">
              <label className="labelCampoHorario">Sala:</label>
              <input
                className="inputCampoHorario"
                placeholder="Digite a sala"
                value={novoHorario.sala}
                onChange={(e) =>
                  setNovoHorario({ ...novoHorario, sala: e.target.value })
                }
              />
            </div>
            <div className="divCampoHorario">
              <label className="labelCampoHorario">Professor:</label>
              <input
                className="inputCampoHorario"
                placeholder="Digite o professor"
                value={novoHorario.professor}
                onChange={(e) =>
                  setNovoHorario({ ...novoHorario, professor: e.target.value })
                }
              />
            </div>
            {editando ? (
              <>
                <button
                  type="button"
                  onClick={confirmarEdicao}
                  className="btnEditarHorario"
                >
                  Confirmar Edição
                </button>
                <button
                  type="button"
                  onClick={cancelarEdicao}
                  className="btnCancelarEdicao"
                >
                  Cancelar Edição
                </button>
              </>
            ) : (
              <button
                type="button"
                onClick={adicionarHorario}
                className="btnSubmitFormHorarios"
              >
                Salvar
              </button>
            )}
          </div>
        </div>
        <div className="containerTabelaHorarios">
          <table>
            <thead>
              <tr>
                <th>Curso</th>
                <th>Período</th>
                <th>Matéria</th>
                <th>Sala</th>
                <th>Professor</th>
                <th></th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {horarios.map((horario, index) => (
                <tr key={index}>
                  <td>{horario.curso}</td>
                  <td>{horario.periodo}</td>
                  <td>{horario.materia}</td>
                  <td>{horario.sala}</td>
                  <td>{horario.professor}</td>
                  <td>
                    <button
                      type="button"
                      onClick={() => preencherCamposDeEdicao(index)}
                    >
                      Editar
                    </button>
                  </td>
                  <td>
                    <button type="button" onClick={() => excluirHorario(index)}>
                      Excluir
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
}

export default FormHorario;
