import React, { useState } from "react";
import Header from "../../component/Header/Header";
import "../../assets/css/FormCurso.css";

function FormCurso() {
  const [cursos, setCursos] = useState([]);
  const [novoCurso, setNovoCurso] = useState({
    nome: "",
    data: "",
    duracao: "",
    coordenador: "",
  });

  const adicionarCurso = async () => {
    try {
      if (novoCurso.nome && novoCurso.data && novoCurso.duracao && novoCurso.coordenador) {
        const response = await fetch('http://localhost:3000/cursos', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(novoCurso),
        });

        if (response.ok) {
          // Se o curso foi adicionado com sucesso, atualiza a lista local
          setCursos([...cursos, novoCurso]);
          // Limpa o estado do novo curso após adicionar
          setNovoCurso({
            nome: '',
            data: '',
            duracao: '',
            coordenador: '',
          });
        } else {
          console.error('Erro ao adicionar curso');
        }
      } else {
        alert('Preencha todos os campos!');
      }
    } catch (error) {
      console.error('Erro ao adicionar curso', error);
    }
  };
  const editarCurso = (index) => {
    console.log(`Editar curso com índice ${index}`);
  };

  const excluirCurso = (index) => {
    const novosCursos = cursos.filter((curso, i) => i !== index);
    setCursos(novosCursos);
  };

  return (
    <>
      <Header />
      <div className="section1FormCursos">
        <h1 className="tituloPagina">Cadastro Curso</h1>
        <div className="conatinerFormCursos">
          <div className="cadastroFormularioCursos">
            <div className="divComboCurso">
              <label className="labelComboCurso">Nome:</label>
              <input
                className="imputComboCurso"
                placeholder="digite nome o curso"
                id="nomeCurso"
                value={novoCurso.nome}
                onChange={(e) => setNovoCurso({ ...novoCurso, nome: e.target.value })}
              />
            </div>
            <div className="divComboCurso">
              <label className="labelComboCurso">Data:</label>
              <input
                className="imputComboCurso"
                placeholder="digite Data o curso"
                id="DataCurso"
                type="datetime"
                value={novoCurso.data}
                onChange={(e) => setNovoCurso({ ...novoCurso, data: e.target.value })}
              />
            </div>
            <div className="divComboCurso">
              <label className="labelComboCurso">Duração:</label>
              <input
                className="imputComboCurso"
                placeholder="digite Duração o curso"
                id="DuracaoCurso"
                value={novoCurso.duracao}
                onChange={(e) => setNovoCurso({ ...novoCurso, duracao: e.target.value })}
              />
            </div>
            <div className="divComboCurso">
              <label className="labelComboCurso">Nome do Coordenador:</label>
              <input
                className="imputComboCurso"
                placeholder="digite nome do Coordenador do curso"
                id="nomeCoodenadorCurso"
                value={novoCurso.coordenador}
                onChange={(e) => setNovoCurso({ ...novoCurso, coordenador: e.target.value })}
              />
            </div>
            <button type="button" className="btnSubmitFormCursos" onClick={adicionarCurso}>
              Salvar
            </button>
          </div>
        </div>
        <div className="containerTabelaCursos">
          <table>
            <thead>
              <tr>
                <th>Nome</th>
                <th>Data</th>
                <th>Duração</th>
                <th>Coordenador</th>
                <th></th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {cursos.map((curso, index) => (
                <tr key={index}>
                  <td>{curso.nome}</td>
                  <td>{curso.data}</td>
                  <td>{curso.duracao}</td>
                  <td>{curso.coordenador}</td>
                  <td>
                    <button type="button">Editar</button>
                  </td>
                  <td>
                    <button type="button">Excluir</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
}

export default FormCurso;
